INSERT INTO `evaluate` VALUES (1,2,'Device that sends information directly to family or cloud for access','Gives measure of wellness','Person who is alone and needs a lot of care','Technology exists; regulation not there;','yes, if adopted','Regulation needed; sensory energy/battery life constraints; '),(2,2,'Caregiver personal contact hybrid','Safety check on older adult;  caregiver acts as a social link','Person in his or her home','Not enough caregivers; high cost','Requires different care model; triage at home','Supply of caregivers is limited'),(3,2,'Communication Voice/Telepresence','Prevents isolation; enables family contact; reduces feeling of distance','In home or at a community center','Acceptance may be a limiting factor','Must be kept simple; use existing and familiar technology (i.e., Television)','Current older adult generation may not accept this technology'),(4,3,'Combined network of information technology and human assistance that will use monitoring (sensors) to track the senior\'s behavior','Quality of life, safety','3G/4G, Network that can intervene','Good, nothing to be invented','ADT, Lease','Acceptance from the senior, access'),(5,4,'Video monitoring','Safety – peace of mind','Caregiver – Patient','Tech exists, high feasiblity','High: evolve/learn trends','Privacy – Patient willingness – Impacts patient less (2nd order effect)'),(6,4,'Wearable monitoring','Safety – peace of mind – health of patient','Patient','Sensors exist – System Integration – High','Good – Design – Fashionable','Compliance – Data Exchange – More Complex Design – Interpreting Data More Complex'),(7,4,'E-Pill Box','Health – Peace of Mind','Both patient and caregiver','Good, tech exists – Product development','High – single product','User loading – can\'t confirm – pills are actually taken'),(8,5,'Individuals in home','Lonliness; Nutrition; Cleaning','The indvidual','Requires more people','+++\'','Unrelated contacts; Turnover; Limited time; Concerns for safety'),(9,5,'Audio augmentation of life','Sound bypasses vision problem; New knowledge; Safe','The indvidual','Inexpensive; Easy to implement','+++++\'','Not real social contact'),(10,5,'Smarter connected homes',NULL,NULL,NULL,NULL,'Cost; Overly complex; Invasive'),(11,6,'Smart pill box','Improve quality of life, lower cost of care (assisted living, ER visits)','Caregivers/PT, Healthcare System','Yes','Yes','Q/A Issues, What happens if something goes wrong, power issues'),(12,6,'Interactive games/Exercise',NULL,'Electronic/In-person varies w/age','Yes','More w.r.t. Time','Interface issues'),(13,7,'Social Network, shared activities from a distance, provide feedback/recording of high points in life','Target depression, isolation, motivation, tie in access to resources','Older adults, their families, community, friends','Tech is already there, simple, interface must be simple --> touch screen or buttons','Must connect with family for sustainability','Requires buy-in, must achieve ease of use'),(14,7,'\"Granny Wash\", chair-like, pleasant, feedback during use','Gets clean and enjoys it. Make them feel clean and attractive','Individuals and their caregivers','Challenges of modifying environment, but solves a major problem','must be low cost, must provide motivation','dependence on technology, cost of overhaul to modify environmetn'),(15,7,'Ease of resource access technology for food, meds, hygiene, entertainment, provides feedback, budgeting','extended independence for individual, ease of mind for family, enables involvement for patient, \"Being useful\"','individual, family, caregivers','easy to implement','built into existing services','cost-effectiveness?, still have to pay for resources'),(16,8,'Monitoring device','Fast response – reassurance for independent living','Elderly – family','Very','Very','Not preventative – Stigma – Adoption problems'),(17,8,'Movement assisting exoskeleton','Prevent falls – improve stability – increase activity','Golf buddies','@ prototype stage','??','Unproven – stigma'),(18,8,'Program to improve social communication and manage electronic life','Increases independence – Improve social well being',NULL,'Very','Very','Adoption problems – Lack of automation (TV better medium)'),(19,9,'Ease-y Chair + Sensor Technology','Comfort, Alignment, Monitoring','Caregivers, Patients, Providers','Alignment?, price is less expensive',NULL,'No social?, consumer price, internet, material-cleaning, info negligence, liability'),(20,9,'Mobile bot + educational/monitoring platform','Depression, monitoring, awareness, social','Caregivers, Patients, Providers, general public','medium --> improving, cheapest',NULL,'Tech, time of physicians to look at info, privacy issues, supply chain'),(21,9,'Sporty-sense','Promoting independence, facilitating active lifestyle, targets depression, social?','Caregivers, Patients, Providers','aesthetics (biggest issue)','Wheel chair, golf cart','automate alerts, tech - late adapter curves');
